import React, { useState, useEffect } from 'react';
import PokeapiService from '../../services/PokeapiService'
import Loading from '../loading'
import ErrorBoundry from '../error-boundry'
import './App.css';

const App = () => {
  const [allPokemons, setAllPokemons] = useState([])
  const pokeapiService = new PokeapiService()
  const { getAllPokemons, getPokemonImage, getPokemon } = pokeapiService
  useEffect(() => {
    async function fetchData() {
      const res = await getAllPokemons()
      setAllPokemons(res)
    }
    fetchData()
  }, [])
  if (!allPokemons) {
    return <Loading />
  }
  return (
    <ErrorBoundry>
      <div className="row">
        {allPokemons.map(pokemon => {
          return (

            <div className="col-sm-3 d-flex align-items-stretch" key={pokemon.id}>
              <div className="card">
                <img className="card-img-top" src={`${pokemon.img}`} alt={`${pokemon.name}`} />
                <div className="card-body">
                  <h5 className="card-title">{pokemon.name}</h5>
                  <p className="card-text">Some quick example text to build on the card title and make up the bulk of the card's content.</p>
                  <a href="#" className="btn btn-primary">Go somewhere</a>
                </div>
              </div>
            </div>
          );
        })}

      </div>
    </ErrorBoundry>
  );
}

export default App;
